INSERT INTO `novel` (`id`, `user_id`, `novelname`, `slug_novelname`, `author`, `slug_author`, `summary`, `novel_views`, `type_id`, `category_id`, `state`, `image`, `status`, `created_at`, `updated_at`) VALUES
(1, 1, 'Dungeon Defense', 'dungeon-defense', 'Yoo Heonhwa', 'yoo-heonhwa', 'Bạn có biết thế giới này sẽ kết thúc như thế nào không?\r\n\r\nTrở thành vị anh hùng vào đánh bại 72 Chúa Quỷ. Trò chơi tư duy chiến thuật với độ khó cao nhất,『Dungeon Attack』.\r\n\r\nTôi đã từng là ‘anh hùng’ và đã phá đảo hết mọi thứ tất tần tật trong cái game đó, nhưng sau khi trả lời cái bảng khảo sát đầy bí ẩn kia, tôi nhận ra bản thần mình đã ở trong trò chơi trong thân xác của tên Chúa Quỷ yếu nhất「Dantalian」.\r\n\r\nChỉ với cái lưỡi giỏi hùng biện cùng với những kí ức đã phá đảo trò chơi khi còn là anh hùng—.\r\n\r\nĐể có thể sống sót trong lốt Chúa Quỷ Dantalian. Tôi sẽ tàn phá thế giới này.', 273, 1, 1, 2, 'dungeon-defense-cover-1-90.png', 0, '2022-06-2 13:39:19', '2022-06-19 23:17:12'),
(2, 1, 'Dị Thế Nhục Hình Công Chúa', 'di-the-nhuc-hinh-cong-chua', 'Ayasato Keishi', 'ayasato-keishi', '<p>&quot;Đ&acirc;y l&agrave; c&acirc;u chuyện về ch&agrave;ng trai trẻ bị kẻ kh&aacute;c giết chết theo c&aacute;ch đầy t&agrave;n bạo, v&agrave; l&agrave; c&acirc;u chuyện về con qu&aacute;i vật đ&atilde; t&agrave;n nhẫn s&aacute;t hại người kh&aacute;c.</p>\r\n\r\n<p>&nbsp; &nbsp; Hay c&oacute; lẽ l&agrave; c&acirc;u chuyện về đứa trẻ bị bỏ rơi bởi cha mẹ m&igrave;nh, v&agrave; về người h&ugrave;ng bị bỏ rơi bởi thế giới.</p>\r\n\r\n<p>&nbsp; &nbsp;D&ugrave; c&oacute; l&agrave; g&igrave; đi chăng nữa, th&igrave; đ&acirc;y cũng l&agrave; một c&acirc;u chuyện về l&ograve;ng ngưỡng mộ v&agrave; sự ngu ngốc.</p>\r\n\r\n<p>&nbsp; &nbsp; Đ&acirc;y l&agrave; một c&acirc;u chuyện về t&igrave;nh y&ecirc;u, nhưng kh&ocirc;ng phải l&agrave; c&acirc;u chuyện về sự l&atilde;ng mạn.</p>\r\n\r\n<p>&nbsp; &nbsp; Một ng&agrave;y n&agrave;o đ&oacute;, hẳn n&oacute; sẽ được nhớ đến như một c&acirc;u chuyện từ thuở xa xưa.</p>\r\n\r\n<p>&nbsp; &nbsp; Một giai thoại nhỏ b&eacute;, kinh khủng v&agrave; bi kịch.</p>\r\n\r\n<p>&nbsp; &nbsp; Thứ qu&aacute; biến chất để c&oacute; thể mạo danh một c&acirc;u chuyện cổ t&iacute;ch.&quot;</p>\r\n\r\n<p>&nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p><em>―Dị thế Nhục h&igrave;nh C&ocirc;ng ch&uacute;a, chương 12, tập 6.</em></p>', 276, 1, 3, 0, 'di-the-nhuc-hinh-cong-chua-cover-3-21.jpg', 0, '2022-06-17 13:39:19', '2022-06-20 02:37:21'),
(4, 1, 'Hãy Hẹn Hò Với Người Cực Kì Đáng Yêu Như Mình Đi!', 'hay-hen-ho-voi-nguoi-cuc-ki-dang-yeu-nhu-minh-di', 'Mikami Kota', 'mikami-kota', 'Tôi nhận được một lời tỏ tình từ cô gái nổi tiếng trong lớp, Yuzu. Đó là: Hãy giả vờ làm người yêu để giải quyết mối tình tay ba.\r\n\r\nTrong lớp học, trước mắt tôi cô ấy cứ như là một con mèo giả nai vậy, thật ra mọi hành động và lời nói của một người tự yêu bản thân này quả thật quá là phiền phức.', 111, 1, 1, 0, 'date-with-cute-me-cover-1-64.jpg', 0, '2022-06-17 13:39:19', '2022-06-19 22:32:51'),
(5, 1, 'Triều Đại Cuối Cùng', 'trieu-dai-cuoi-cung', 'Michael Yichao', 'michael-yichao', 'Hai lần tử, ba lần sinh, Mordekaiser là tên chúa tể tàn bạo từ một kỷ nguyên xa xôi, kẻ nắm giữ tà thuật với khả năng nô lệ linh hồn. Giờ thì ít ai còn nhớ những cuộc chinh phục thời đầu của hắn, hay giới hạn sức mạnh của hắn nằm ở đâu—nhưng vẫn còn đó một vài linh hồn cổ xưa ghi khắc nỗi kinh hoàng này, và họ khiếp sợ cái ngày hắn trở lại thống trị cả người sống lẫn kẻ chết.', 30, 1, 3, 1, 'trieu-dai-cuoi-cung-cover-24.jpg', 0, '2022-06-17 13:39:19', '2022-06-19 22:32:37'),
(6, 1, 'Người Đàn Ông Cầm Cây Gậy Thép', 'nguoi-dan-ong-cam-cay-gay-thep', 'Odin Austin Shafer', 'odin-austin-shafer', 'Nếu bạn đến bất kỳ ngôi làng nào trên khắp chốn Ionia, bạn cũng có thể được nghe kể lại câu chuyện về Vụ Bắt Giữ Ác Quỷ Hoàng Kim. Được ghi lại trong rất nhiều vở kịch và thơ văn, kỳ công bắt giữ linh hồn tà ác này vẫn còn được tôn vinh đến tận ngày nay.\r\n\r\nTuy nhiên, mỗi câu chuyện cổ tích đều có một phần cốt lõi được lấy từ sự thật, và sự thật về Ác Quỷ Hoàng Kim thì khác xa so với câu chuyện được kể lại.\r\n\r\n“Ác Quỷ Hoàng Kim” khét tiếng thực chất chỉ là một tay dựng sân khấu nhỏ nhoi trong các đoàn kịch rong, các gánh hát dạo ở Zhuyn, và tên hắn là Khada Jhin.\r\n\r\nDường như Runeterra cũng chỉ là một tấm vải họa cho thứ nghệ thuật kinh tởm của Jhin, và giờ chỉ mỗi hắn biết nét bút tiếp theo sẽ được đặt tại nơi nào.', 13, 2, 3, 1, 'nguoi-dan-ong-cam-cay-gay-thep-cover-39.jpg', 0, '2022-06-17 13:39:19', '2022-06-19 22:32:24'),
(7, 1, 'Lưỡi Hái Ngàn Năm', 'luoi-hai-ngan-nam', 'Michael Mccarthy', 'michael-mccarthy', 'Ánh thép lóa lên, lọt vào mắt Zed. Một đứa trẻ chưa lên mười nằm trong đống bùn, cây liềm chỉ thẳng vào tên thích khách bậc thầy, những ngón tay đẫm máu siết chặt đến trắng bệch. Đôi mắt thằng bé chất chứa nỗi đau khó lòng thấy được ở tuổi này, và cháy bỏng cơn cuồng nộ của một chiến binh dạn dày trận mạc. Sự kiên cường này không phải thứ được chỉ dạy mà thành. Zed nhìn thấy ở đứa trẻ sống sót bị bỏ lại này một thứ vũ khí có thể dùng để chống lại chính những kẻ đã đưa nó tới đây. Hắn chìa tay chào đón Kayn đến Hội Bóng Tối.', 8, 2, 3, 1, 'luoi-hai-ngan-nam-cover-53.jpg', 0, '2022-06-17 13:39:19', '2022-06-19 22:32:07'),
(12, 1, 'Vivy: Fluorite Eye\'s Song - Prototype', 'vivy-fluorite-eyes-song-prototype', 'Tappei Nagatsuki & Eiji Umehara', 'tappei-nagatsuki-eiji-umehara', 'Nierland — một công viên chủ đề phức hợp A.I, nơi giao thoa giữa những giấc mơ, hy vọng và khoa học. Được tạo ra với tư cách là A.I hình người tự trị đầu tiên, Vivy hoạt động như một A.I diễn viên cho công viên. Để hoàn thành sứ mệnh của mình là làm cho mọi người hạnh phúc thông qua các bài hát, cô ấy tiếp tục lên sân khấu và biểu diễn với tất cả trái tim của mình. Tuy nhiên, công viên giải trí vẫn chưa được phổ biến rộng rãi. Một ngày nọ, một A.I tên là Matsumoto xuất hiện trước Vivy và giải thích rằng anh ta đã du hành từ 100 năm đến tương lai, với sứ mệnh sửa chữa lịch sử cùng Vivy và ngăn chặn cuộc chiến giữa A.I và loài người sẽ diễn ra vào 100 năm sau. Cuộc gặp gỡ của hai A.I với những nhiệm vụ khác nhau sẽ được vẽ lại trong tương lai nào? Đây là câu chuyện A.I tiêu diệt A.I. Hành trình 100 năm của diva A.I Vivy bắt đầu.', 104, 1, 3, 0, 'vivy-fluorite-eyes-song-prototype-cover-1-7.jpg', 0, '2022-06-17 13:39:19', '2022-06-19 22:31:55'),
(13, 1, 'Trở Thành Thế Lực Trong Màn Đêm', 'tro-thanh-the-luc-trong-man-dem', 'Akasatana', 'akasatana', 'Như bao người ngưỡng mộ những anh hùng khi còn nhỏ, nhân vật chính của chúng ta lại ngưỡng mộ những con người trong bóng tối. Sau khi giấu đi khả năng thật sự và sống 1 cuộc đời tầm thường của 1 thường dân vào ban ngày trong khi trải qua những khóa huấn luyện điên rồ vào ban đêm, anh ta cuối cùng cũng được chuyển sinh sang thê giới khác. Người đàn ông ấy, người mà chỉ phô diễn sức mạnh trong bóng tối,những người hầu cận, và cùng với đó là tổ chức lớn mạnh của anh…\r\n\r\nĐây là câu chuyện về một chàng trai, người luôn khao khát sức mạnh trong màn đêm, trị vì thế giới ngầm ở thế giới khác.', 2, 2, 1, 0, 'tro-thanh-the-luc-trong-man-dem-cover-1-45.jpg', 0, '2022-06-17 13:39:19', '2022-06-19 22:31:37'),
(14, 1, 'Tinh Linh Huyễn Tưởng Ký', 'tinh-linh-huyen-tuong-ky', 'Kitayama Yuri', 'kitayama-yuri', 'Như bao người ngưỡng mộ những anh hùng khi còn nhỏ, nhân vật chính của chúng ta lại ngưỡng mộ những con người trong bóng tối. Sau khi giấu đi khả năng thật sự và sống 1 cuộc đời tầm thường của 1 thường dân vào ban ngày trong khi trải qua những khóa huấn luyện điên rồ vào ban đêm, anh ta cuối cùng cũng được chuyển sinh sang thê giới khác. Người đàn ông ấy, người mà chỉ phô diễn sức mạnh trong bóng tối,những người hầu cận, và cùng với đó là tổ chức lớn mạnh của anh…\r\n\r\nĐây là câu chuyện về một chàng trai, người luôn khao khát sức mạnh trong màn đêm, trị vì thế giới ngầm ở thế giới khác.', 142, 1, 1, 0, 'tinh-linh-huyen-tuong-ky-cover-14-84.jpg', 0, '2022-06-17 13:39:19', '2022-06-19 22:31:18'),
(15, 1, 'Ma Kiếm Sư tại Học Viện Thánh Kiếm', 'ma-kiem-su-tai-hoc-vien-thanh-kiem', 'Shimizu Yuu', 'shimizu-yuu', 'Ma Vương mạnh nhất Leonis phong ấn sự tồn tại của mình lại trong hơn 1000 năm để chuẩn bị cho trận chiến cuối cùng trong tương lai. Tuy nhiên, sau giấc ngủ ngàn năm, hắn ta đột trở về với thân hình của một cậu bé 10 tuổi!\r\n\r\n\"Tại sao em lại bị nhốt ở đây? Mọi chuyện ổn rồi. Chị sẽ bảo vệ em.\"\r\n\r\nĐược cô gái xinh đẹp Lyseria bao bọc, Ma Vương Leonis bắt đầu cuộc hành trình của mình tại Học Viện Thánh Kiếm.', 268, 1, 1, 0, 'ma-kiem-su-tai-hoc-vien-kiem-thanh-cover-9-86.jpg', 0, '2022-06-17 13:39:19', '2022-06-19 22:31:01'),
(16, 1, 'Rokudenashi Majutsu Koushi to Akashic Records', 'rokudenashi-majutsu-koushi-to-akashic-records', 'Tarou Hitsuji', 'tarou-hitsuji', 'Sisti bước vào học viện ma pháp nhằm trau dồi kĩ năng ma thuật của của bản thân cùng với hi vọng giải đáp bí ẩn của Lâu đài trên mây. Sau khi giảng viên yêu thích của cô rời bỏ công việc thì thay thế vào vị trí đó, Glenn hóa ra lại là tên lười biếng, chậm chạp, xem ra là một tên khốn giảng viên vô năng. Làm sao mà Glenn lại là người được pháp sư mạnh nhất học viện đích thân đề cử cơ chứ?', 159, 1, 1, 0, 'Rokudenashi-Majutsu-Koushi-to-Akashic-Records-cover-6-4.jpg', 0, '2022-06-17 13:39:19', '2022-06-19 22:30:49'),
(17, 1, 'Công Chúa Điện Hạ Dường Như Đang Rất Tức Giận', 'cong-chua-dien-ha-duong-nhu-dang-rat-tuc-gian', 'Yatsuhashi Kou', 'yatsuhashi-kou', 'Sinh ra là Công chúa Chiến tranh, được ca tụng là Pháp sư vĩ đại nhất, tên cô là Leticiel...\r\n\r\nCô mất đi gia đình và người thương trong cuộc xâm lược của Vương quốc láng giềng. Để ngăn việc sức mạnh của mình rơi vào tay kẻ địch, Leticiel quyết định tự vẫn, hay đó là những gì được cho là đã xảy ra, thay vào đó, cô lại thức dậy và thấy trước mắt là một trần nhà xa lạ.\r\n\r\nSau khi thu thập thông tin, Leticiel nhận ra rằng bằng cách nào đấy linh hồn mình đã nhập vào thân xác của cô gái khác một ngàn năm sau.\r\n\r\nCàng tệ hơn, người này là một đứa con gái bất tài, bị mọi người, gia đình và cả vị hôn phu xa lánh.\r\n\r\nCó nhiều điều mà cô muốn phàn nàn, nhưng khi chứng kiến Ma thuật của thế giới này một ngàn năm sau… “Ma thuật sư điên cuồng” Leticiel chết lặng vì độ tệ hại của chúng.\r\n\r\n\"Cấp độ Ma thuật ở thế giới này quá thấpppppp!!!!\"\r\n\r\nCâu chuyện về nàng Công chúa lập dị khuấy đảo lối sống thường nhật bằng Ma thuật mạnh nhất, bắt đầu!!!', 7, 1, 8, 0, 'cong-chua-dien-ha-duong-nhu-dang-rat-tuc-gian-cover-1-50.jpeg', 0, '2022-06-17 13:39:19', '2022-06-19 22:30:27'),
(18, 1, 'Cổ Luật Ma Pháp Kỵ Sĩ', 'co-luat-ma-phap-ky-si', 'Hitsuji Tarou', 'hitsuji-tarou', '“Một Kỵ sĩ chỉ nói sự thật.”\r\n\r\n“Một lòng dũng cảm luôn bùng cháy trong tim.”\r\n\r\n“Một thanh gươm bảo vệ kẻ yếu.”\r\n\r\n“Một sức mạnh cho những điều thiện lành.”\r\n\r\n“Một cơn cuồng nộ – Tận diệt cái ác.”\r\n\r\n---Sid Brace, Kỵ sĩ mạnh nhất kỉ nguyên huyền thoại, hay còn được biết dưới danh <Mọi rợ>\r\n\r\nAnh được hồi sinh bởi vị ‘Hoàng tử’ trẻ tuổi của xứ Calvania và được bổ nhiệm làm Giảng viên tại Học viện Ma pháp Kỵ sĩ.\r\n\r\nCó bốn lớp học về ý niệm của Kỵ sĩ, nhưng trớ trêu thay, anh lại bị xếp vào lớp mang tên của mình.\r\n\r\n“Không thấy nhục nhã khi tự gọi mình là một Kỵ sĩ sao? – Bỏ kiếm xuống.”\r\n\r\nVị Kỵ sĩ tốt nhất lại là một kẻ man di! Chuỗi ngày làm ‘Giảng viên’ chính thức bắt đầu!', 266, 1, 1, 0, 'co-luat-ma-phap-ky-si-cover-1-65.jpg', 0, '2022-06-17 13:39:19', '2022-06-19 22:30:09'),
(19, 1, 'Có Nhỏ Vợ Cũ Hồi Xuân Trong Lớp Tôi', 'co-nho-vo-cu-hoi-xuan-trong-lop-toi', 'Nekomata Nuko', 'nakomata-nuko', 'Gặp lại người vợ cũ đã chia tay, để rồi yêu lại một lần nữa--\r\n\r\nMột bộ romcom bắt đầu từ chuyện ly dị.\r\n\r\nChuyện chưa kể rằng, ngày đó, những người bạn cùng lớp gặp lại nhau trên đại học sau khi tốt nghiệp, trở thành bạn bè, trở thành người yêu, rồi trở thành vợ chồng với nhau...\r\n\r\n「Có hối hận thì cũng trễ rồi nhé! 」\r\n\r\n「Có khóc cũng đã muộn màng rồi nhé!」\r\n\r\nTrên đường về sau khi vừa nộp đơn ly hôn, Kurose Kouhei và Koikawa Yuzuka bất chợt xuyên không về quá khứ, đúng cái ngày cả hai nhập học cao trung. Để có một cuộc đời hạnh phúc, cả đôi vợ chồng cũ đã hứa với lòng rằng 「sẽ chẳng bao giờ liên quan đến đối phương nữa」--\r\n\r\nNhưng mà, do có cùng sở thích nên rằng đi đâu họ cũng chạm mặt nhau. Lúc ban đầu thì ghét ở cùng nhau đấy, để rồi khi ngồi xem phim trên ghế dành cho đôi tình nhân...họ lại tận hưởng lại những ngày tháng đã cùng hạnh phúc bên nhau, cảm xúc dễ chịu ấy lại bắt đầu ùa về...', 90, 1, 1, 1, 'co-nho-vo-cu-hoi-xuan-trong-lop-toi-cover-1-34.jpg', 0, '2022-06-17 13:39:19', '2022-06-19 22:29:55'),
(23, 1, 'Testing', 'testing', 'Testing', 'testing', 'asdasdasd', 29, 2, 1, 0, 'readmore-90.jpg', 0, '2022-06-18 20:12:18', '2022-06-19 22:50:05');

